import React from 'react'
import axios from 'axios';
const AttendenceMark = () => {
let response,studentId;
  const fetchStudentDetails = async () => {
       response = await axios.get('/attendances/:id', async (req, res) => {
        try {
          studentId = req.query.studentId;
          console.log(studentId);
          
    } catch (error) {
      console.error('Error fetching student details:', error);
    }})
  };
  console.log(response,studentId);

  fetchStudentDetails();

  const markAttendance = studentId => {
    axios.post('https://localhost:3000/attendances', {
      studentId,
      status: 'present',
    })
      .then(response => {
        console.log('Attendance marked successfully:', response.data);
      })
      .catch(error => {
        console.error('Error marking attendance:', error);
      });
  };

  return (
    <div>AttendenceMark</div>
  )
}

export default AttendenceMark
