import React, { useState, useEffect } from 'react';
import axios from 'axios';
import QrReader from 'react-qr-reader';

function AttendanceMark() {
  const [students, setStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState('');
  const [status, setStatus] = useState('present');
  const [scanResult, setScanResult] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:3000/students')
      .then(response => {
        setStudents(response.data);
      })
      .catch(error => {
        console.error('Error fetching students:', error);
      });
  }, []);

  const handleMarkAttendance = () => {
    if (selectedStudent && status) {
      axios.post('http://localhost:3000/attendances', {
        studentId: selectedStudent,
        status,
      })
        .then(response => {
          console.log('Attendance marked successfully:', response.data);
        })
        .catch(error => {
          console.error('Error marking attendance:', error);
        });
    } else {
      alert('Please select a student and attendance status.');
    }
  };

  const handleScan = data => {
    if (data) {
      setScanResult(data);
      // You can parse the scanned data and take appropriate actions
      // For example, if the data contains student ID, you can set the selected student
      setSelectedStudent(data);
    }
  };

  const handleError = error => {
    console.error('Error scanning QR code:', error);
  };

  return (
    <div>
      <h2>Mark Attendance</h2>
      <label>Select Student:</label>
      <select onChange={(e) => setSelectedStudent(e.target.value)}>
        <option value="">Select</option>
        {students.map(student => (
          <option key={student._id} value={student._id}>{student.Name}</option>
        ))}
      </select>
      <br />
      <label>Attendance Status:</label>
      <select onChange={(e) => setStatus(e.target.value)}>
        <option value="present">Present</option>
        <option value="absent">Absent</option>
      </select>
      <br />
      <button onClick={handleMarkAttendance}>Mark Attendance</button>

      <hr />

      <h3>QR Code Scanner</h3>
      <QrReader
        delay={300}
        onError={handleError}
        onScan={handleScan}
        style={{ width: '100%' }}
      />

      {scanResult && (
        <div>
          <h4>Scanned Result:</h4>
          <p>{scanResult}</p>
        </div>
      )}
    </div>
  );
}

export default AttendanceMark;
