import React, { useEffect, } from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

const About = () => {
  const navigate = useNavigate();

  const [updateData, setUpdateData] = useState(false);

  const [userData, setUserData] = useState({
    Name: "",
    Course: "",
    Phone: "",
    Batch: ""
  });

  const [newUserData, setNewUserData] = useState({
    Name: "",
    Course: "",
    Phone: "",
    Batch: ""
  });

  // const updateBtn = document.getElementById('updatebtn')

  const callAboutPage = async () => {
    try {
      const res = await fetch('/about', {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json"
        },
        credentials: "include"
      });

      const data = await res.json();
      // console.log(data);
      setUserData(data);
      // console.log("UserData:",userData.name);

      if (!res.status === 200) {
        const error = new Error(res.error);
        throw error;
      }

    } catch (error) {
      console.log(`Catched Error:${error}`);
      // alert("Login First");
      navigate('/login');
    }
  }


  let name, value;
  const InputChange = (e) => {
    name = e.target.name;
    value = e.target.value;
    setNewUserData({ ...newUserData, [name]: value });
  }

  //switching 
  const changeData = (e) => {

    setUpdateData(true);
    //  status=true;
  }

  const saveData = async (e) => {
    e.preventDefault();
    try {
      const { Name, Course, Phone, Batch } = newUserData;

      if (!Name || !Course || !Phone || !Batch){
       alert('Please fill All the Fields !!');
    }else{
      const res = await fetch("/update", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          Name, Course, Phone, Batch
        })
      });

      const data = res.json();
      // console.log(data);
      if (!data) {
        alert("user not updated")
        console.log("user not updated");
      } else {
        alert("user updated")
        setNewUserData({
          Name: "",
          Course: "",
          Phone: "",
          Batch: ""
        })
        setUpdateData(false);
      }
    }
    }
     catch (error) {
          console.log(error);
        }




}



      useEffect(() => {
        // callAboutPage();
      }, []);

      return (
        <div>
          {

            (!updateData) ?

              <section className="text-gray-600 body-font">
                <div className="container px-5 py-20 mx-auto bg-gray-100 flex flex-col">
                  <div className="lg:w-4/6 bg-white px-10  mx-auto">
                    {/* <div className="rounded-lg h-64 overflow-hidden">

      </div> */}
                    <div className="flex flex-col sm:flex-row mt-10">
                      <div className="m-auto sm:w-2/3 sm:pl-8 sm:py-8 sm:border-l border-gray-200 sm:border-t-0 border-t mt-4 pt-4 sm:mt-0 text-center sm:text-left">
                        <div className='flex'>
                          <p className='font-bold text-gray-700 my-2'>Name :</p>
                          <p className='font-bold text-gray-500 m-2'>{userData.Name}</p>
                        </div>
                        <p className='font-bold text-gray-900 my-2'>Contact </p>
                        <div className="w-full  ">
                          <div className='flex'>
                            <p className='font-bold text-gray-400 m-2'>Course : </p>
                            <p className='font-bold text-gray-500 m-2'>{userData.Course}</p>
                          </div>
                          <div className='flex'>
                            <p className='font-bold text-gray-400 m-2'>Batch : </p>
                            <p className='font-bold text-gray-500 m-2'>{userData.Batch}</p>
                          </div>
                        </div>
                
                        <button id="savebtn" onClick={changeData} className="float-right mt-4 text-white px-4 py-1 rounded-full inline-flex items-center bg-indigo-500 ">Update
                          <svg fill="none" stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" className="w-4 h-4 ml-2" viewBox="0 0 24 24">
                            <path d="M5 12h14M12 5l7 7-7 7"></path>
                          </svg>
                        </button>

                      </div>
                    </div>
                  </div>
                </div>
              </section>

              :

              <section className="text-gray-600 body-font">
                <div className="container px-5 py-24 mx-auto bg-gray-100 flex flex-col">
                  <div className="lg:w-4/6 bg-white px-10  mx-auto">
                    {/* <div className="rounded-lg h-64 overflow-hidden">

      </div> */}
                    <div className="flex flex-col sm:flex-row mt-10">
           
                      <div className="m-auto sm:w-2/3 sm:pl-8 sm:py-8 sm:border-l border-gray-200 sm:border-t-0 border-t mt-4 pt-4 sm:mt-0 text-center sm:text-left">
                        <p className='font-bold text-gray-900 my-2'>Name</p>
                        <div className="w-full flex flex-row ">
                          <input value={newUserData.Name} onChange={InputChange} name='Name' contentEditable="false" autoComplete='true' type="text" placeholder="Name" className="block  text-sm py-3 px-4 rounded-lg w-1/2 border outline-none" />
                          <input value={newUserData.Course} onChange={InputChange} name='Course' contentEditable="false" autoComplete='true' type="text" placeholder="Course " className="block ml-2  text-sm py-3 px-4 rounded-lg w-1/2 border outline-none" />
                        </div>
                        <p className='font-bold text-gray-900 my-2'>Contact</p>
                        <div className="w-full flex flex-row ">
                          <input value={newUserData.Phone} onChange={InputChange} name='Phone' autoComplete='true' type="text" placeholder="Batch" className="block  text-sm py-3 px-4 rounded-lg w-1/2 border outline-none" />
                          <input value={newUserData.Batch} onChange={InputChange} name='Batch' autoComplete='true' type="text" placeholder="Batch " className="block ml-2  text-sm py-3 px-4 rounded-lg w-1/2 border outline-none" />
                        </div>
                        <p className='font-bold text-gray-900 my-2'>More</p>
                        <div className="w-full flex basis-1/2 m-auto ">
                          <input name='age' value={newUserData.age} onChange={InputChange} autoComplete='true' type="text" placeholder="Age" className="block  text-sm py-3 px-4 rounded-lg w-1/2 border outline-none" />
                          <input name='gender' value={newUserData.gender} onChange={InputChange} autoComplete='true' type="text" placeholder="Gender" className="block  ml-2 text-sm py-3 px-4 rounded-lg w-1/2 border outline-none" />
                          <input name='dob' value={newUserData.dob} onChange={InputChange} autoComplete='true' type="text" placeholder="DOB" className="block  ml-2 text-sm py-3 px-4 rounded-lg w-1/2 border outline-none" />
                        </div>
                        <button id="savebtn" onClick={saveData} className="float-right  mt-4 text-white px-4 py-1 rounded-full inline-flex items-center bg-indigo-500 ">Save
                          <svg fill="none" stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" className="w-4 h-4 ml-2" viewBox="0 0 24 24">
                            <path d="M5 12h14M12 5l7 7-7 7"></path>
                          </svg>
                        </button>

                      </div>
                    </div>
                  </div>
                </div>
              </section>

          }
        </div>
      )
    }

export default About
