import { Link } from 'react-router-dom';
import {useState} from 'react';
// import {useHistory} from 'react';
import "./Student.css";

const Student = () => {
    
    // const history = useHistory();
    const [user, setUser] = useState({
        name:"",
        course:"",
        phone:"",
        batch:"",
    });

    let name,value;
    const InputChange = (e) =>{
        name = e.target.name;
        value = e.target.value;
        setUser({...user,[name]:value});
    }

    const postData = async (e) =>{
        e.preventDefault();
        const {name,course,phone,batch}=user;
        const res = await fetch("/add",{
            method:"POST",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                name,course,phone,batch
            })
        })
        const data = await res.json();
        console.log(data);

        if(res.status === 422 || !data){
            window.alert("Invalid Registration");
        }else{
            window.alert("Registration success..");
            // history.push("/login")
        }
    }
  return (
    <div className=" main relative h-screen overflow-hidden  bg-purple-400 flex justify-center items-center">
    <div className=" absolute w-60 h-60 rounded-xl bg-purple-300 -top-5 -left-16  transform rotate-45 hidden md:block">
    </div>
    <div className="absolute w-48 h-48 rounded-xl bg-purple-300 -bottom-6 -right-10 transform rotate-12 hidden md:block">
    </div>
    <div className="py-12 px-12 bg-white rounded-2xl shadow-xl z-20">
        <div>
            <h1 className="text-3xl font-bold text-left mb-4 cursor-pointer">Add <span className='text-purple-300 drop-shadow-lg'>Student</span></h1>
          
        </div>
        <div className="space-y-4">
            <input name="name" onChange={InputChange} value={user.name} autoComplete='true' type="text" placeholder="Name" className="block text-lg py-3 px-4 rounded-lg w-full border outline-none" />
            <input name="course" onChange={InputChange} value={user.course} autoComplete='true' type="text" placeholder="Course" className="block text-lg py-3 px-4 rounded-lg w-full border outline-none" />
            <input name="phone" onChange={InputChange} value={user.phone} autoComplete='true' type="text" placeholder="Phone" className="block text-lg py-3 px-4 rounded-lg w-full border outline-none" />
            <input name="batch" onChange={InputChange} value={user.batch} autoComplete='true' type="text" placeholder="Batch" className="block text-lg py-3 px-4 rounded-lg w-full border outline-none" />
        </div>
        <div className="text-center mt-6">
            <button onClick={postData} className="py-3 w-64 text-xl text-white bg-purple-400 transition ease-in-out duration-500  hover:bg-purple-500  rounded-2xl">Add Student</button>
           
        </div>
    </div>
    <div className="w-40 h-40 absolute bg-purple-300 rounded-full -top-10 right-12 hidden md:block"></div>
    <div
        className="w-20 h-40 absolute bg-purple-300 rounded-full bottom-20 left-10 transform rotate-45 hidden md:block">
    </div>
</div>
  )
}

export default Student
