import { useState } from 'react';
import axios from 'axios';
import './Student.css';

axios.defaults.baseURL = 'http://localhost:3000/';

const Student = () => {
  const [user, setUser] = useState({
    name: '',
    course: '',
    phone: '',
    batch: '',
  });

  const InputChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const postData = async (e) => {
    e.preventDefault();
  
    try {
      const response = await axios.post('/add', user);
      console.log('Response from server:', response.data);
  
      // Reset the form or perform other actions based on the response
      setUser({
        name: '',
        course: '',
        phone: '',
        batch: '',
      });
    } catch (error) {
      console.error('Error posting data:', error);
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.error('Server response data:', error.response.data);
        console.error('Server response status:', error.response.status);
        console.error('Server response headers:', error.response.headers);
      } else if (error.request) {
        // The request was made but no response was received
        console.error('No response received from the server');
      } else {
        // Something happened in setting up the request that triggered an Error
        console.error('Error setting up the request:', error.message);
      }
    }
  };
  

  return (
    <div className="main relative h-screen overflow-hidden bg-purple-400 flex justify-center items-center">
      {/* ... (existing styling) */}
      <div className="py-12 px-12 bg-white rounded-2xl shadow-xl z-20">
        <div>
          <h1 className="text-3xl font-bold text-left mb-4 cursor-pointer">
            Add <span className="text-purple-300 drop-shadow-lg">Student</span>
          </h1>
        </div>
        <div className="space-y-4">
          <input
            name="name"
            onChange={InputChange}
            value={user.name}
            autoComplete="true"
            type="text"
            placeholder="Name"
            className="block text-lg py-3 px-4 rounded-lg w-full border outline-none"
          />
          <input
            name="course"
            onChange={InputChange}
            value={user.course}
            autoComplete="true"
            type="text"
            placeholder="Course"
            className="block text-lg py-3 px-4 rounded-lg w-full border outline-none"
          />
          <input
            name="phone"
            onChange={InputChange}
            value={user.phone}
            autoComplete="true"
            type="text"
            placeholder="Phone"
            className="block text-lg py-3 px-4 rounded-lg w-full border outline-none"
          />
          <input
            name="batch"
            onChange={InputChange}
            value={user.batch}
            autoComplete="true"
            type="text"
            placeholder="Batch"
            className="block text-lg py-3 px-4 rounded-lg w-full border outline-none"
          />
        </div>
        <div className="text-center mt-6">
          <button
            onClick={postData}
            className="py-3 w-64 text-xl text-white bg-purple-400 transition ease-in-out duration-500  hover:bg-purple-500  rounded-2xl"
          >
            Add Student
          </button>
        </div>
      </div>
    </div>
  );
};

export default Student;
