import React from 'react'
import axios from 'axios';
const AttendenceMark = () => {
let response,studentId;
  const fetchStudentDetails = async () => {
       response = await axios.get('/attendances/:id', async (req, res) => {
        try {
          studentId = req.query.studentId;
          
    } catch (error) {
      console.error('Error fetching student details:', error);
    }})
  };

  fetchStudentDetails();

 axios.get('/attendances', async (req, res) => {
    try {
  
      // Update the student's presence status in the database
      const updatedStudent = await response.findOneAndUpdate(
        { studentId },
        { present: 'present' },
        { new: true }
      );
  
      if (!updatedStudent) {
        return res.status(404).json({ message: 'Student not found' });
      }
  
      return res.json(updatedStudent);
    } catch (error) {
      console.error(error);
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  return (
    <div>AttendenceMark</div>
  )
}

export default AttendenceMark
