import { Link } from 'react-router-dom';
import {useState} from 'react';
// import {useHistory} from 'react';
import "./Student.css";

const Student = () => {
    
    // const history = useHistory();
    const [user, setUser] = useState({
        name:"",
        email:"",
        password:"",
        confirm_password:""
    });

    let name,value;
    const InputChange = (e) =>{
        name = e.target.name;
        value = e.target.value;
        setUser({...user,[name]:value});
    }

    const postData = async (e) =>{
        e.preventDefault();
        const {name,email,password,confirm_password}=user;
        const res = await fetch("/register",{
            method:"POST",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                name,email,password,confirm_password
            })
        })
        const data = await res.json();
        console.log(data);

        if(res.status === 422 || !data){
            window.alert("Invalid Registration");
        }else{
            window.alert("Registration success..");
            // history.push("/login")
        }
    }
  return (
    <div className=" relative h-screen overflow-hidden  bg-purple-400 flex justify-center items-center">
    <div className=" absolute w-60 h-60 rounded-xl bg-purple-300 -top-5 -left-16  transform rotate-45 hidden md:block">
    </div>
    <div className="absolute w-48 h-48 rounded-xl bg-purple-300 -bottom-6 -right-10 transform rotate-12 hidden md:block">
    </div>
    <div className="py-12 px-12 bg-white rounded-2xl shadow-xl z-20">
        <div>
            <h1 className="text-3xl font-bold text-center mb-4 cursor-pointer">Create An <span className='text-purple-300 drop-shadow-lg'>Account</span></h1>
            <p className="w-80 text-center text-sm mb-8 font-semibold text-gray-700 tracking-wide cursor-pointer">Create an
                account to enjoy all the services without any ads for free!</p>
        </div>
        <div className="space-y-4">
            <input name="name" onChange={InputChange} value={user.name} autoComplete='true' type="text" placeholder="Your Name" className="block text-sm py-3 px-4 rounded-lg w-full border outline-none" />
            <input name="email" onChange={InputChange} value={user.email} autoComplete='true' type="text" placeholder="Email Address" className="block text-sm py-3 px-4 rounded-lg w-full border outline-none" />
            <input name="password" onChange={InputChange} value={user.password} autoComplete='true' type="text" placeholder="Password" className="block text-sm py-3 px-4 rounded-lg w-full border outline-none" />
            <input name="confirm_password" onChange={InputChange} value={user.confirm_password} autoComplete='true' type="text" placeholder="Confirm Password" className="block text-sm py-3 px-4 rounded-lg w-full border outline-none" />
        </div>
        <div className="text-center mt-6">
            <button onClick={postData} className="py-3 w-64 text-xl text-white bg-purple-400 transition ease-in-out duration-500  hover:bg-purple-500  rounded-2xl">Create Account</button>
            <p className="mt-4 text-sm">Already Have An Account? <span className="underline cursor-pointer transition ease-in-out duration-500 hover:text-purple-500"><Link to="/login">Sign In</Link></span>
            </p>
        </div>
    </div>
    <div className="w-40 h-40 absolute bg-purple-300 rounded-full -top-10 right-12 hidden md:block"></div>
    <div
        className="w-20 h-40 absolute bg-purple-300 rounded-full bottom-20 left-10 transform rotate-45 hidden md:block">
    </div>
</div>
  )
}

export default Student
