import React, { useEffect } from 'react';
import axios from 'axios';

const AttendenceMark = () => {
  const fetchStudentDetails = async () => {
    try {
      const studentId = 'your_student_id'; // Replace with your logic to get studentId
      console.log(studentId);

      const response = await axios.get(`/attendances/:id?studentId=${studentId}`);
      console.log('Student details fetched successfully:', response.data);
    } catch (error) {
      console.error('Error fetching student details:', error);
    }
  };

  const markAttendance = studentId => {
    axios.post('https://localhost:3000/attendances', {
      studentId,
      status: 'present',
    })
      .then(response => {
        console.log('Attendance marked successfully:', response.data);
      })
      .catch(error => {
        console.error('Error marking attendance:', error);
      });
  };

  useEffect(() => {
    fetchStudentDetails();
  }, []); // Run once when the component mounts

  return (
    <div>AttendanceMark</div>
  );
};

export default AttendenceMark;
