import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AttendenceMark = () => {
  const [studentId, setStudentId] = useState('');

  const fetchStudentDetails = async () => {
    try {
      // Replace this logic to get the actual student ID from the URL parameter
      const urlSearchParams = new URLSearchParams(window.location.search);
      const urlStudentId = urlSearchParams.get('id');

      if (!urlStudentId) {
        console.error('Student ID is missing in the URL.');
        return;
      }

      setStudentId(urlStudentId);

      const response = await axios.get(`http://localhost:3001/students/${urlStudentId}`);
      console.log('Student details fetched successfully:', response.data);
    } catch (error) {
      console.error('Error fetching student details:', error);
    }
  };

  const markAttendance = async () => {
    try {
      // Ensure that studentId is not an empty string
      if (!studentId) {
        console.error('Student ID is missing.');
        return;
      }

      const response = await axios.post('http://localhost:3001/attendances', {
        student: studentId,
        status: 'present',
      });

      console.log('Attendance marked successfully:', response.data);
    } catch (error) {
      console.error('Error marking attendance:', error);
    }
  };

  useEffect(() => {
    fetchStudentDetails();
  }, []); // Run once when the component mounts

  useEffect(() => {
    // Mark attendance only when studentId is available
    if (studentId) {
      markAttendance();
    }
  }, [studentId]);

  return (
    <div>
      <h2>Attendance Mark</h2>
      <p>Student ID: {studentId}</p>
    </div>
  );
};

export default AttendenceMark;
