import Home from './Pages/Home/Home';
import Student from './Pages/Student/Student';
import Error from './Pages/Error/Error';
import Header from './Components/Header/Header';
import Attendence from './Pages/Attendence/Attendence';
import Update from "./Pages/Update/Update"
import AttendanceMark from "./Components/AttendanceMark/AttendanceMark"
import AttendanceView from "./Components/AttendanceView/AttendanceView"
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import "./index.css";
function App() {S
  return (
    <>
    <div className='main'>
      <Router>
      <Header/>
      <Routes>
        <Route path='/' element={<Home/>}></Route>
        <Route path='/add' element={<Student/>}></Route>
        <Route path='/student/:id' Component={Update} element={<Update/>}></Route>
        <Route path='/attendence' element={<Attendence/>}></Route>
        <Route path="/mark-attendance" element={<AttendanceMark />}></Route>
        <Route path="/view-attendance" element={<AttendanceView />}></Route>
        <Route path='*' element={<Error/>}></Route>
      </Routes>
      </Router>
      </div>
    </>
  );
}

export default App;
