import React, { useState } from 'react';
import axios from 'axios';
import { QrReader } from 'react-qr-reader';

function AttendanceMark() {
  const [scanResult, setScanResult] = useState(null);

  const handleScan = data => {
    if (data) {
      setScanResult(data);
      console.log(scanResult);
      processAttendanceURL(data);
    }
  };

  const handleError = error => {
    console.error('Error scanning QR code:', error);
  };

  const processAttendanceURL = url => {
    // Extract the studentId from the URL
    const studentId = extractStudentIdFromURL(url);
    // const studentId = "657f1dd0ca290924ecb7c7d6";

    // Mark attendance for the student
    markAttendance(studentId);
  };

  const extractStudentIdFromURL = url => {
    // Extract studentId from the URL (assuming studentId is in the query parameter)
    const urlParams = new URLSearchParams(new URL(url).search);
    return urlParams.get('studentId');
  };

  const markAttendance = studentId => {
    axios.post('https://localhost:3000/attendances', {
      studentId,
      status: 'present',
    })
      .then(response => {
        console.log('Attendance marked successfully:', response.data);
      })
      .catch(error => {
        console.error('Error marking attendance:', error);
      });
  };
handleScan()
  // markAttendance();

  return (
    <div>
      <h2>Mark Attendance</h2>

      {/* <QrReader
        delay={300}
        onError={handleError}
        onScan={handleScan}
        style={{ width: '100%' }}
      /> */}
      {/* <button
        delay={300}
       onError={handleError}
        onClick={handleScan}
    >Scan Stud</button> */}

      {scanResult && (
        <div>
          <h4>Scanned Result:</h4>
          <p>{scanResult}</p>
        </div>
      )}
    </div>
  );
}

export default AttendanceMark;
